(function () {
  if (window.imageCompression) {
    window.imageCompressionReady = Promise.resolve(window.imageCompression);
    return;
  }

  window.imageCompressionReady = new Promise((resolve, reject) => {
    const script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/browser-image-compression@2.0.2/dist/browser-image-compression.min.js';
    script.async = true;
    script.onload = () => resolve(window.imageCompression);
    script.onerror = () => reject(new Error('Не удалось загрузить browser-image-compression'));
    document.head.appendChild(script);
  });
})();
